factory RailData.forExperience(
    UserExperience experience, double screenWidth,
  ) => switch (experience) {
      UserExperience.mobile => RailData._mobile(
        screenWidth, tilesInRow: 3, textScale: 1,
      ),
      UserExperience.tv => RailData._tv(
        screenWidth, tilesInRow: 8, textScale: 1.5,
      ),
    };
  }
